import { View, Text } from 'react-native';
import {
  Table,
  TableWrapper,
  Row,
  Rows,
  Col,
  Cols,
  Cell,
} from 'react-native-table-component';
import useCityFruits from './src/hooks/useCityFruits';

export default function Dashboard() {
  // return a response table with the following columns:
  // - city
  // - fruit
  // - quantity

  return (
    <View className="flex flex-col justify-center items-center">
      <Text className="text-lg font-bold text-gray-700">Dashboard</Text>
      <View className="pt-10">
        <Text className="text-lg font-bold text-gray-700">City</Text>
      </View>
      <View className="pt-10">
        <Text className="text-lg font-bold text-gray-700">Fruit</Text>
      </View>
      <View className="pt-10">
        <Text className="text-lg font-bold text-gray-700">Quantity</Text>
      </View>
      <DataTable />
    </View>
  );
}

function DataTable() {
  const { fruits, cities, loading } = useCityFruits();
  return (
    <View className="w-full">
      <Table borderStyle={{ borderWidth: 1 }}>
        <Row data={['', ...fruits]} />
        <TableWrapper>
          <Col data={[...cities]} heightArr={[28, 28]} />
          <Rows data={[]} />
        </TableWrapper>
      </Table>
    </View>
  );
}
