import { useState } from 'react';
import { View, Text, TouchableOpacity, Image, TextInput } from 'react-native';

export default function Login({ navigation }) {
  const [signInView, setSignInView] = useState(false);

  return !signInView ? (
    <View className="flex-1">
      <View className="rounded-lg w-6/12 h-3/5">
        <Image className="" source={require('.,/assets/logo.png')} />
      </View>

      <View className="flex flex-col items- justify-center">
        <Text className="text-lg text-gray-700 text-center font-bold p-3">Fruitmark</Text>
        <Text className="text-lg text-gray-700 text-center font-normal p-3">
          We deliver on-demand organic fresh fruits from nearby stores
        </Text>
      </View>

      <View className="flex flex-col items-center justify-center">
        <View className="w-72 my-4">
          <TouchableOpacity
            className="bg-yellow-200 rounded-lg shadow-2xl p-2"
            onPress={() => {
              navigation.navigate('Dashboard');
            }}
          >
            <Text className="text-gray-700 text-center text-lg font-semibold">
              Get Started
            </Text>
          </TouchableOpacity>
        </View>
        <View className="w-72">
          <TouchableOpacity
            className="bg-white rounded-lg shadow-2xl p-2"
            onPress={() => setSignInView(true)}
          >
            <Text className="text-gray-700 text-center text-lg font-semibold">
              I'm a Client
            </Text>
          </TouchableOpacity>
        </View>
      </View>
    </View>
  ) : (
    <SignIn setSignInView={setSignInView} />
  );
}

function SignIn({ setSignInView }) {
  return (
    <View className="flex-1 flex-col justify-center items-center p-3 m-3">
      <View>
        <Text className="text-gray-700 text-lg font-bold text-center">
          Sign In
        </Text>
      </View>

      <View>
        <TextInput
          placeholder="Email Address"
          className="bg-white rounded-lg shadow-lg p-2 m-4 text-gray-700"
        />
        <TextInput
          placeholder="Password"
          className="bg-white rounded-lg shadow-lg p-2 m-4 text-gray-700"
        />
      </View>

      <View className="">
        <TouchableOpacity className="bg-yellow-200 rounded-lg shadow-2xl p-2 m-2">
          <Text className="text-gray-700 text-center text-md font-semibold">
            Sign In
          </Text>
        </TouchableOpacity>
        <TouchableOpacity
          className="bg-white rounded-lg shadow-2xl p-2 m-2"
          onPress={() => {
            setSignInView(false);
          }}
        >
          <Text className="text-gray-700 text-center text-md font-semibold">
            Later
          </Text>
        </TouchableOpacity>
      </View>
    </View>
  );
}

