import { View, Text } from 'react-native';
import {
  Table,
  TableWrapper,
  Row,
  Rows,
  Col,
  Cols,
  Cell,
} from 'react-native-table-component';
import useCityFruits from './src/hooks/useCityFruits';

export default function Dashboard() {
  // return a response table with the following columns:
  // - city
  // - fruit
  // - quantity

  return (
    <View className="flex flex-col justify-center items-center">
      <DataTable />
    </View>
  );
}

function DataTable() {
  const { fruits, cities, loading, cityFruits } = useCityFruits();
  return (
    <View className="w-full">
      <View>
        <Text className="text-lg font-bold text-gray-700">{cityFruit.city[0]}</Text>
        <View>
          {fruits.map((fruit) => (
            <Text className="text-md font-semibold text-gray-700">{fruit}</Text>
          ))}
        </View>
      </View>
    </View>
  );
}
