import { useState } from 'react';
import { StatusBar } from 'expo-status-bar';
import { Text, TouchableOpacity, View, FlatList } from 'react-native';
import { NativeWindStyleSheet } from 'nativewind';

const cities = [
  { name: 'Dijon' },
  { name: 'Lila' },
  { name: 'Marsela' },
  { name: 'Niza' },
  { name: 'Paris' },
];

const fruits = [
  { name: 'Apple', quantity: 10 },
  { name: 'Banana', quantity: 20 },
  { name: 'Orange', quantity: 30 },
  { name: 'Pineapple', quantity: 40 },
];

export default function App() {
  return (
    <View className="container flex-1 bg-white min-w-full">
      <StatusBar style="auto" />
      <View className="pt-10">
        <Transfer label="From" />
      </View>
      <View className="pt-10">
        <Transfer label="To" />
      </View>
    </View>
  );
}

function Transfer({ label }) {
  const [selected, setSelected] = useState(cities[0]);
  const [open, setOpen] = useState(false);

  function handleSelect(city) {
    setSelected(city);
    setOpen(false);
  }

  return (
    <View className="bg-white items-center justify-center">
      <Text>{label}</Text>
      <View className="w-72">
        <View className="mt-1">
          <TouchableOpacity
            onPress={() => setOpen(!open)}
            className="relative w-full cursor-default rounded-lg bg-white py-2 pl-3 pr-10 text-left shadow-md focus:outline-none focus-visible:border-indigo-500 focus-visible:ring-2 
              focus-visible:ring-white focus-visible:ring-opacity-75 focus-visible:ring-offset-2 focus-visible:ring-offset-orange-300 sm:text-sm text-l"
          >
            <View className="flex flex-row justify-between">
              <Text> {selected.name} </Text>
              {/* <Image
                source={require('./assets/chevron-down.svg')}
                className="w-5 h-5"
              /> */}
            </View>
          </TouchableOpacity>
          {open && (
            <FlatList
              className="mt-1 w-full rounded-md bg-white py-1 shadow-lg"
              data={cities}
              renderItem={({ item, index }) => (
                <TouchableOpacity
                  key={index}
                  onPress={() => {
                    handleSelect(item);
                  }}
                >
                  {/* {selected === item ? (
                    <Image
                      source={require('./assets/check-icon.svg')}
                      className="w-5 h-5 absolute inset-y-0 left-0 items-center pl-3 text-amber-600"
                    />
                  ) : null} */}
                  <Text
                    className={`block truncate py-2 pl-10 pr-4 ${
                      selected === item
                        ? 'font-medium text-amber-900 bg-amber-100'
                        : 'font-normal text-gray-900'
                    }`}
                  >
                    {item.name}
                  </Text>
                </TouchableOpacity>
              )}
            />
          )}
        </View>
      </View>
    </View>
  );
}

NativeWindStyleSheet.setOutput({
  default: 'native',
});

