import { View, Text, ScrollView } from 'react-native';
import {
  Table,
  TableWrapper,
  Row,
  Rows,
  Col,
  Cols,
  Cell,
} from 'react-native-table-component';
import useCityFruits from './src/hooks/useCityFruits';

export default function Dashboard() {
  // return a response table with the following columns:
  // - city
  // - fruit
  // - quantity

  return (
    <View className="flex flex-col justify-center items-center">
      <DataTable />
    </View>
  );
}

function DataTable() {
  const { fruits, cities, loading, cityFruits } = useCityFruits();

  return (
    <ScrollView
      contentContainerStyle={{
        alignItems: 'center',
        justifyContent: 'center',
        paddingVertical: '20%',
      }}
      className="w-full flex flex-col"
    >
      {cityFruits.map((item, index) => (
        <View
          key={index}
          className="my-2 border-2 shadow-xl rounded-xl w-11/12 border-red-100 p-2 bg-red-400"
        >
          <View className="justify-center items-center">
            <Text className="text-lg font-bold text-white">
              {item?.city}
            </Text>
          </View>
          <View>
            {item.fruits.map((_item, _index) => (
              <>
                <View
                  key={_index}
                  className="flex flex-row border-2 rounded-lg border-red-300 p-2 m-1"
                >
                  <Text className="text-md font-semibold text-white">
                    {_item?.quantity}{' '}
                  </Text>
                  <Text className="text-md font-semibold text-white">
                    {_item?.fruit}'s
                  </Text>
                </View>
              </>
            ))}
            <View className="flex flex-row justify-end p-2">
              <Text className="text-md font-semibold text-white">
                Total: {item?.sum}
              </Text>
            </View>
          </View>
        </View>
      ))}
    </ScrollView>
  );
}
