import { View, Text } from 'react-native';

export default function Login() {
  return (
    <View>
      <View className="">
        <Text>Logo here</Text>
      </View>
      <View className="flex flex-row">
        <Text>Hey, Welcome</Text>
        <Text>We deliver on-demand organic fresh fruits from nearby stores</Text>
      </View>
    </View>
  );
}
