import { View, Text } from 'react-native';
import {
  Table,
  TableWrapper,
  Row,
  Rows,
  Col,
  Cols,
  Cell,
} from 'react-native-table-component';
import useCityFruits from './src/hooks/useCityFruits';

export default function Dashboard() {
  // return a response table with the following columns:
  // - city
  // - fruit
  // - quantity

  return (
    <View className="flex flex-col justify-center items-center">
      <Text className="text-xl font-bold text-gray-700">Dashboard</Text>
      <DataTable />
    </View>
  );
}

function DataTable() {
  const { fruits, cities, loading, cityFruits } = useCityFruits();

  console.log({ cityFruits });

  return (
    <View className="w-full flex flex-col justify-center items-center">
      {cityFruits.map((item, index) => (
        <View key={index} className="my-2 border-2 shadow-lg rounded-lg w-6/12 border-gray-300 p-2">
          <Text className="text-lg font-bold text-gray-700">{item?.city}</Text>
          <View>
            {item.fruits.map((_item, _index) => (
              <View key={_index}  className="flex flex-row">
                <Text className="text-md font-semibold text-gray-700">
                  {_item?.quantity}{' '}
                </Text>
                <Text className="text-md font-semibold text-gray-700">
                  {_item?.fruit}'s
                </Text>
              </View>
            ))}
          </View>
        </View>
      ))}
    </View>
  );
}
