import { useState } from 'react';
import { Text, TouchableOpacity, View, FlatList } from 'react-native';
import { NativeWindStyleSheet } from 'nativewind';
import { useAtom, atom } from 'jotai';
import useCityFruits from './src/hooks/useCityFruits';

const fromCityAtom = atom('');
const toCityAtom = atom('');
const fruitAtom = atom('');
const quantityAtom = atom(0);

export default function TransferFruits() {
  const { cities, fruits } = useCityFruits();

  return (
    <View className="flex flex-col justify-center items-center">
      <Text className="text-lg font-bold text-gray-700">Transfer</Text>
      <View className="pt-10">
        <Transfer label="From" data={cities} />
      </View>
      <View className="pt-10">
        <Transfer label="To" data={cities} />
      </View>
      <View className="pt-10">
        <Transfer label="Fruit" data={fruits} />
      </View>
      <View className="pt-10 w-6/12">
        <TouchableOpacity
          onPress={() => {
            console.log('transfer');
          }}
          className="bg-blue-500 py-2 px-4 rounded-lg w-full shadow-lg"
        >
          <Text className="text-white text-center">Transfer</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
}

function Transfer({ label, data }) {
  const [selected, setSelected] = useState('Select');
  const [open, setOpen] = useState(false);

  function handleSelect(value) {
    if (label === 'From') {
      fromCityAtom(value);
    } else if (label === 'To') {
      toCityAtom(value);
    } else {
      fruitAtom(value);
    }
    setSelected(value);
    setOpen(false);
  }

  return (
    <View className="bg-white items-center justify-center">
      <Text className="text-md text-gray-700 font-semibold">{label}</Text>
      <View className="w-72">
        <View className="mt-1">
          <TouchableOpacity
            onPress={() => setOpen(!open)}
            className="relative w-full rounded-lg bg-white py-2 pl-3 pr-10 text-left shadow-md text-l"
          >
            <View className="flex flex-row justify-between">
              <Text> {selected ? selected : ''} </Text>
            </View>
          </TouchableOpacity>
          {open && (
            <FlatList
              className="mt-1 w-full rounded-md bg-white py-1 shadow-lg"
              data={data}
              renderItem={({ item, index }) => (
                <TouchableOpacity
                  key={index}
                  onPress={() => {
                    handleSelect(item);
                  }}
                >
                  <Text
                    className={`block truncate py-2 pl-10 pr-4 ${
                      selected === item
                        ? 'font-medium text-amber-900 bg-amber-100'
                        : 'font-normal text-gray-900'
                    }`}
                  >
                    {data ? item : ''}
                  </Text>
                </TouchableOpacity>
              )}
            />
          )}
        </View>
      </View>
    </View>
  );
}

NativeWindStyleSheet.setOutput({
  default: 'native',
});
