import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, TouchableOpacity, View } from 'react-native';
import { NativeWindStyleSheet } from 'nativewind';

const cities = [
  { name: 'Dijon' },
  { name: 'Lila' },
  { name: 'Marsela' },
  { name: 'Niza' },
  { name: 'Paris' },
];

export default function App() {
  const [selected, setSelected] = useState(cities[0]);

  function handleSelect(city) {
    setSelected(city);
    //setOpen(false);
  }

  return <TransferModal />;

  return (
    <View style={styles.container}>
      <Text>Open up App.js to start working on your app!</Text>
      <StatusBar style="auto" />
    </View>
  );
}

function TransferModal() {
  return (
    <View style={styles.container}>
      <TouchableOpacity
        onPress={() => setOpen(!open)}
        className="relative w-full cursor-default rounded-lg bg-white py-2 pl-3 pr-10 text-left shadow-md focus:outline-none focus-visible:border-indigo-500 focus-visible:ring-2 
              focus-visible:ring-white focus-visible:ring-opacity-75 focus-visible:ring-offset-2 focus-visible:ring-offset-orange-300 sm:text-sm text-l"
      >
        <View className="flex flex-row justify-between">
          <Text> {selected.name} </Text>
        </View>
      </TouchableOpacity>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  button: {
    backgroundColor: 'orange',
    padding: 10,
    width: '100%',
    borderRadius: '5rem',
  },
});
