import { View, Text, ScrollView } from 'react-native';
import {
  Table,
  TableWrapper,
  Row,
  Rows,
  Col,
  Cols,
  Cell,
} from 'react-native-table-component';
import useCityFruits from './src/hooks/useCityFruits';

export default function Dashboard() {
  // return a response table with the following columns:
  // - city
  // - fruit
  // - quantity

  return (
    <View className="flex flex-col justify-center items-center">
      <DataTable />
    </View>
  );
}

function DataTable() {
  const { fruits, cities, loading, cityFruits } = useCityFruits();

  return (
    <ScrollView
      contentContainerStyle={{
        alignItems: 'center',
        justifyContent: 'center',
        paddingVertical: '20%',
      }}
      className="w-full flex flex-col"
    >
      {cityFruits.map((item, index) => (
        <View
          key={index}
          className="my-2 border-2 shadow-lg rounded-lg w-6/12 border-gray-300 p-2"
        >
          <Text className="text-lg font-bold text-gray-700">{item?.city}</Text>
          <View>
            {item.fruits.map((_item, _index) => (
              <>
                <View key={_index} className="flex flex-row border-t-2 border-gray-100">
                  <Text className="text-md font-semibold text-gray-700">
                    {_item?.quantity}{' '}
                  </Text>
                  <Text className="text-md font-semibold text-gray-700">
                    {_item?.fruit}'s
                  </Text>
                </View>
              </>
            ))}
            <Text className="text-md font-semibold text-gray-700">
              Total: {item?.sum}
            </Text>
          </View>
        </View>
      ))}
    </ScrollView>
  );
}
