import { StatusBar } from 'expo-status-bar';
import { View, TouchableOpacity, Text } from 'react-native';
import { NativeWindStyleSheet } from 'nativewind';
import TransferFruits from './TransferFruits';

export default function App() {
  return (
    <View className="container flex-1 justify-center bg-white min-w-full">
      <StatusBar style="auto" />
      <TransferFruits />
      <View className="pt-10 w-6/12">
        <TouchableOpacity
          onPress={() => {
            transferFruit();
          }}
          className="bg-blue-500 py-2 px-4 rounded-lg w-full shadow-lg"
        >
          <Text className="text-white text-center">Transfer</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
}

NativeWindStyleSheet.setOutput({
  default: 'native',
});
