import { useState } from 'react';
import {
  View,
  Text,
  TouchableOpacity,
  Image,
  Modal,
  TextInput,
} from 'react-native';

export default function Login() {
  const [modalVisible, setModalVisible] = useState(false);

  return <SignIn />;

  return (
    <View className="flex-1">
      <View className="rounded-lg w-6/12 h-3/5">
        <Image className="" source={require('./assets/logo.png')} />
      </View>

      <View className="flex flex-col items- justify-center">
        <Text className="text-lg text-center font-bold p-3">Hey, Welcome</Text>
        <Text className="text-lg text-center font-normal p-3">
          We deliver on-demand organic fresh fruits from nearby stores
        </Text>
      </View>

      <View className="flex flex-col items-center justify-center">
        <View className="w-72 my-4">
          <TouchableOpacity className="bg-yellow-200 rounded-lg shadow-2xl p-2">
            <Text className="text-gray-700 text-center text-lg font-semibold">
              Get Started
            </Text>
          </TouchableOpacity>
        </View>
        <View className="w-72">
          <TouchableOpacity
            className="bg-white rounded-lg shadow-2xl p-2"
            onPress={() => setModalVisible(true)}
          >
            <Text className="text-gray-700 text-center text-lg font-semibold">
              I'm a Client
            </Text>
          </TouchableOpacity>
        </View>
      </View>
    </View>
  );
}

function SignIn() {
  return (
    <View className="">
      <View>
        <View>
          <TouchableOpacity onPress={() => {}}>
            <Text>Close</Text>
          </TouchableOpacity>
        </View>
        <View>
          <Text>Sign In</Text>
        </View>
        <View>
          <TextInput />
          <TextInput />
        </View>
      </View>
    </View>
  );
}

